import React, { useState } from 'react';
import '../styles/CustomerForm.css';

const CustomerForm = () => {
  const [formData, setFormData] = useState({
    FirstName: '',
    SecondName: '',
    Email: '',
    PhoneNumber: '',
    Address: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5267/api/Customers', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
      if (!response.ok) {
        throw new Error('Failed to send data');
      }
      const data = await response.json();
      alert('Дані надіслано!');
    } catch (error) {
      console.error('Error:', error);
      alert('Помилка при відправці даних');
    }
  };

  return (
    <div className="form-container">
    <><h1 style={{ textAlign: 'center', color: '#333' }}>Customer form</h1>
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <label>First name</label>
        <input
          type="text"
          name="FirstName"
          value={formData.FirstName}
          onChange={handleChange}
        />
      </div>
      <div className="form-group">
        <label>Second name</label>
        <input
          type="text"
          name="SecondName"
          value={formData.SecondName}
          onChange={handleChange}
        />
      </div>
      <div className="form-group">
        <label>Email</label>
        <input
          type="email"
          name="Email"
          value={formData.Email}
          onChange={handleChange}
        />
      </div>
      <div className="form-group">
        <label>Phone number</label>
        <input
          type="tel"
          name="PhoneNumber"
          value={formData.PhoneNumber}
          onChange={handleChange}
        />
      </div>
      <div className="form-group">
        <label>Address</label>
        <input
          type="text"
          name="Address"
          value={formData.Address}
          onChange={handleChange}
        />
      </div>
      <button type="submit" className="submit-btn">Надіслати</button>
    </form>
    </>
    </div>
  );
};

export default CustomerForm;
