import React, { useState } from 'react';
import background from './assets/background.png'; // Імпорт зображення

function App() {
  const [formData, setFormData] = useState({
    "Firstname": '',
    "Second name": '',
    email: '',
    phone: '',
    address: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
    alert('Дані надіслано! Перевірте консоль.');
  };

  return (
    <div
      style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        backgroundImage: `url(${background})`, // Фон із імпортованим зображенням
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        fontFamily: 'Arial, sans-serif',
      }}
    >
      <div
        style={{
          padding: '20px',
          borderRadius: '8px',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
          backgroundColor: '#ffffffcc',
          maxWidth: '400px',
          width: '100%',
        }}
      >
        <h1 style={{ textAlign: 'center', color: '#333' }}>Customer form</h1>
        <form onSubmit={handleSubmit}>
          {/* Поля форми */}
          <button
            type="submit"
            style={{
              width: '100%',
              padding: '10px',
              backgroundColor: '#007bff',
              color: '#fff',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
              fontWeight: 'bold',
              transition: 'background-color 0.3s',
            }}
            onMouseEnter={(e) => (e.target.style.backgroundColor = '#90ee90')}
            onMouseLeave={(e) => (e.target.style.backgroundColor = '#007bff')}
          >
            Надіслати
          </button>
        </form>
      </div>
    </div>
  );
}

export default App;
